import axios from 'axios';
import { AnimatePresence, motion } from 'framer-motion';
import { useEffect, useRef, useState } from 'react';
import Button from 'react-bootstrap/Button';
import { Link, Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import './App.css';

const getRandomReference = () => {
  const books = ['john', 'psalms', 'proverbs', 'romans', 'genesis', 'matthew'];
  const randomBook = books[Math.floor(Math.random() * books.length)];
  const randomChapter = Math.floor(Math.random() * 10) + 1;
  const randomVerse = Math.floor(Math.random() * 20) + 1;
  return { book: randomBook, chapter: randomChapter, verse: randomVerse };
};

const versionMap = {
  en: 'kjv',
  te: 'te-IN-irvtel',
  hi: 'hi-IN-irvhin'
};

const VerseBox = ({ verse, reference, onSpeak, onStop, isSpeaking }) => (
  <div>
    <div className="verse-text mb-3" style={{ minHeight: '100px', fontSize: '1.25rem', color: '#333', fontStyle: 'italic' }}>
      <AnimatePresence mode="wait">
        <motion.p
          key={verse}
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -10 }}
          transition={{ duration: 0.6 }}
        >
          ‚Äú{verse}‚Äù
        </motion.p>
      </AnimatePresence>
    </div>
    <div className="text-end fst-italic fw-semibold text-muted mb-3">‚Äî {reference}</div>
    <Button variant="info" onClick={isSpeaking ? onStop : onSpeak}>
      {isSpeaking ? 'üîá Stop' : 'üîä Speak'}
    </Button>
  </div>
);

const RandomVerseGenerator = ({ darkMode, toggleDarkMode }) => {
  const [verse, setVerse] = useState('');
  const [reference, setReference] = useState('');
  const [language, setLanguage] = useState('en');
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem('favorites')) || []);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const synthRef = useRef(window.speechSynthesis);
  const utteranceRef = useRef(null);

  useEffect(() => {
    fetchVerse();
  }, [language]);

  const fetchVerse = async () => {
    const { book, chapter, verse } = getRandomReference();
    const version = versionMap[language] || 'kjv';
    const url = `https://cdn.jsdelivr.net/gh/wldeh/bible-api/bibles/${version}/books/${book}/chapters/${chapter}/verses/${verse}.json`;

    try {
      const res = await axios.get(url);
      const verseText = res.data.text;
      const ref = `${res.data.book.name} ${res.data.chapter}:${res.data.verse}`;
      setVerse(verseText);
      setReference(ref);
    } catch (err) {
      console.error("Verse fetch error:", err);
      setVerse("Verse not available.");
      setReference(`${book.toUpperCase()} ${chapter}:${verse}`);
    }
  };

  const addToFavorites = () => {
    const fav = `${verse} - ${reference}`;
    if (!favorites.includes(fav)) {
      const updated = [...favorites, fav];
      setFavorites(updated);
      localStorage.setItem('favorites', JSON.stringify(updated));
    }
  };

  const speakVerse = () => {
    stopSpeaking();
    const utterance = new SpeechSynthesisUtterance(verse);
    utterance.lang = language;
    utterance.onend = () => setIsSpeaking(false);
    synthRef.current.speak(utterance);
    utteranceRef.current = utterance;
    setIsSpeaking(true);
  };

  const stopSpeaking = () => {
    synthRef.current.cancel();
    setIsSpeaking(false);
  };

  return (
    <div className={`d-flex justify-content-center align-items-center min-vh-100 ${darkMode ? 'bg-dark text-white' : 'bg-light'}`}>
      <div className={`verse-box p-5 rounded-4 shadow-lg text-center ${darkMode ? 'bg-secondary' : 'bg-white'}`} style={{ maxWidth: '700px', width: '90%' }}>
        <h1 className="title mb-4" style={{
          background: 'linear-gradient(to right, #00c6ff, #0072ff)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          fontWeight: 'bold',
          fontSize: '2.5rem'
        }}>
          Verse of Hope
        </h1>

        <VerseBox
          verse={verse}
          reference={reference}
          onSpeak={speakVerse}
          onStop={stopSpeaking}
          isSpeaking={isSpeaking}
        />

        <div className="d-flex justify-content-center flex-wrap gap-2 mb-3">
          <select value={language} onChange={(e) => setLanguage(e.target.value)} className="form-select w-auto">
            <option value="en">English</option>
            <option value="te">Telugu</option>
            <option value="hi">Hindi</option>
          </select>
          <Button variant="primary" onClick={addToFavorites}>üíæ Save</Button>
          <Button variant="outline-dark" onClick={toggleDarkMode}>{darkMode ? '‚òÄÔ∏è Light' : 'üåô Dark'}</Button>
          <Link to="/favorites"><Button variant="success">View Favorites</Button></Link>
        </div>

        <Button
          className="px-4 py-2"
          variant="primary"
          onClick={fetchVerse}
          style={{
            borderRadius: '25px',
            fontWeight: 'bold',
            background: 'linear-gradient(to right, #00c6ff, #0072ff)',
            border: 'none',
            transition: 'all 0.3s ease'
          }}
        >
          Generate Verse
        </Button>
      </div>
    </div>
  );
};

const FavoritesPage = () => {
  const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
  const [searchTerm, setSearchTerm] = useState('');
  const filteredFavorites = favorites.filter(fav => fav.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <div className="container py-5">
      <h2 className="text-center mb-4" style={{
        background: 'linear-gradient(to right, #00c6ff, #0072ff)',
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        fontWeight: 'bold',
        fontSize: '2.5rem'
      }}>üíñ Favorite Verses</h2>

      {favorites.length > 0 && (
        <div className="mb-4">
          <input
            type="search"
            className="form-control w-75 mx-auto"
            placeholder="Search your favorites..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      )}

      {favorites.length === 0 ? (
        <div className="text-center p-5 bg-light rounded-4 shadow-sm">
          <h3>No Saved Verses Yet</h3>
          <p className="text-muted">Your favorite verses will appear here once you save them.</p>
        </div>
      ) : (
        <div className="row g-4">
          {filteredFavorites.map((fav, index) => {
            const [text, reference] = fav.split(' - ');
            return (
              <div className="col-md-6" key={`${text}-${reference}`}>
                <div className="card h-100 shadow-sm hover-shadow transition-all">
                  <div className="card-body text-center">
                    <p className="card-text mb-3" style={{ fontSize: '1.1rem', fontStyle: 'italic' }}>
                      "{text}"
                    </p>
                    <p className="card-subtitle text-muted">
                      ‚Äî {reference}
                    </p>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}

      <div className="text-center mt-5">
        <Link to="/">
          <Button variant="primary" size="lg" className="px-4 py-2" style={{
            borderRadius: '25px',
            background: 'linear-gradient(to right, #00c6ff, #0072ff)',
            border: 'none',
            transition: 'all 0.3s ease'
          }}>
            ‚¨Ö Back to Generator
          </Button>
        </Link>
      </div>
    </div>
  );
};

const App = () => {
  const [darkMode, setDarkMode] = useState(false);
  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<RandomVerseGenerator darkMode={darkMode} toggleDarkMode={toggleDarkMode} />} />
        <Route path="/favorites" element={<FavoritesPage />} />
      </Routes>
    </Router>
  );
};

export default App;
