import { AnimatePresence, motion } from 'framer-motion';
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import './App.css';
import verses from './data/verses.json';

const shuffleArray = (array) => {
  const arr = [...array];
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
};

const VerseOfHope = () => {
  const [shuffledVerses, setShuffledVerses] = useState([]);
  const [verseIndex, setVerseIndex] = useState(0);
  const [verse, setVerse] = useState('');

  useEffect(() => {
    const newVerses = shuffleArray(verses);
    setShuffledVerses(newVerses);
    setVerse(newVerses[0]);
    setVerseIndex(1);
  }, []);

  const generateNewVerse = () => {
    if (verseIndex >= shuffledVerses.length) {
      const reshuffled = shuffleArray(verses);
      setShuffledVerses(reshuffled);
      setVerse(reshuffled[0]);
      setVerseIndex(1);
    } else {
      setVerse(shuffledVerses[verseIndex]);
      setVerseIndex((prev) => prev + 1);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center min-vh-100 bg-light">
      <div
        className="verse-box p-5 rounded-4 shadow-lg text-center bg-white"
        style={{ maxWidth: '700px', width: '90%' }}
      >
        <h1
          className="title mb-4"
          style={{
            background: 'linear-gradient(to right, #00c6ff, #0072ff)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 'bold',
            fontSize: '2.5rem',
          }}
        >
          Verse of Hope
        </h1>

        <div
          className="verse-text mb-4"
          style={{
            minHeight: '120px',
            fontSize: '1.2rem',
            color: '#333',
            fontStyle: 'italic',
          }}
        >
          <AnimatePresence mode="wait">
            <motion.p
              key={verse}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.6 }}
            >
              {verse}
            </motion.p>
          </AnimatePresence>
        </div>

        <Button
          className="px-4 py-2"
          variant="primary"
          onClick={generateNewVerse}
          style={{
            borderRadius: '25px',
            fontWeight: 'bold',
            background: 'linear-gradient(to right, #00c6ff, #0072ff)',
            border: 'none',
            transition: 'all 0.3s ease',
          }}
          onMouseEnter={(e) =>
            (e.target.style.boxShadow = '0 8px 20px rgba(0, 114, 255, 0.3)')
          }
          onMouseLeave={(e) => (e.target.style.boxShadow = 'none')}
        >
          Generate Verse
        </Button>
      </div>
    </div>
  );
};

export default VerseOfHope;
