import axios from 'axios';
import { AnimatePresence, motion } from 'framer-motion';
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import './App.css';
import verses from './data/verses.json';

const shuffleArray = (array) => {
  const arr = [...array];
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
};

const RandomVerseGenerator = () => {
  const [shuffledVerses, setShuffledVerses] = useState([]);
  const [verseIndex, setVerseIndex] = useState(0);
  const [verse, setVerse] = useState('');
  const [translatedVerse, setTranslatedVerse] = useState('');
  const [language, setLanguage] = useState('en');
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem('favorites')) || []);

  useEffect(() => {
    const newVerses = shuffleArray(verses);
    setShuffledVerses(newVerses);
    setVerse(newVerses[0]);
    setVerseIndex(1);
  }, []);

  useEffect(() => {
    if (verse) translateVerse(verse);
  }, [verse, language]);

  const translateVerse = async (text) => {
    if (language === 'en') {
      setTranslatedVerse(text);
      return;
    }

    try {
      const response = await axios.post('https://libretranslate.de/translate', {
        q: text,
        source: 'en',
        target: language === 'te' ? 'hi' : language,
        format: 'text'
      }, {
        headers: {
          'Content-Type': 'application/json',
          'accept': 'application/json'
        }
      });

      setTranslatedVerse(response.data.translatedText);
    } catch (error) {
      console.error('LibreTranslate failed:', error?.response?.data || error.message);
      setTranslatedVerse(text); // fallback
    }
  };

  const generateNewVerse = () => {
    if (verseIndex >= shuffledVerses.length) {
      const reshuffled = shuffleArray(verses);
      setShuffledVerses(reshuffled);
      setVerse(reshuffled[0]);
      setVerseIndex(1);
    } else {
      setVerse(shuffledVerses[verseIndex]);
      setVerseIndex((prev) => prev + 1);
    }
  };

  const addToFavorites = () => {
    const updated = [...favorites, verse];
    setFavorites(updated);
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  return (
    <div className="d-flex justify-content-center align-items-center min-vh-100"
         style={{ background: 'linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%)' }}>
      <div className="verse-box p-5 rounded-4 shadow-lg text-center bg-white" style={{ maxWidth: '700px', width: '90%' }}>
        <h1 className="title mb-4"
            style={{
              background: 'linear-gradient(to right, #00c6ff, #0072ff)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              fontWeight: 'bold',
              fontSize: '2.5rem'
            }}>
          Verse of Hope
        </h1>

        <div className="verse-text mb-4"
             style={{ minHeight: '120px', fontSize: '1.2rem', color: '#333', fontStyle: 'italic' }}>
          <AnimatePresence mode="wait">
            <motion.p
              key={translatedVerse}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.6 }}
            >
              {translatedVerse}
            </motion.p>
          </AnimatePresence>
        </div>

        <div className="d-flex justify-content-center gap-2 mb-3">
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            className="form-select w-auto"
          >
            <option value="en">English</option>
            <option value="hi">Hindi</option>
            <option value="te">Telugu (via Hindi)</option>
          </select>

          <Button variant="secondary" onClick={addToFavorites}>‚≠ê Save</Button>
        </div>

        <Button
          className="px-4 py-2"
          variant="primary"
          onClick={generateNewVerse}
          style={{
            borderRadius: '25px',
            fontWeight: 'bold',
            background: 'linear-gradient(to right, #00c6ff, #0072ff)',
            border: 'none',
            transition: 'all 0.3s ease'
          }}
        >
          Generate Verse
        </Button>
      </div>
    </div>
  );
};

export default RandomVerseGenerator;
