import axios from 'axios';
import { AnimatePresence, motion } from 'framer-motion';
import { useEffect, useRef, useState } from 'react';
import Button from 'react-bootstrap/Button';
import { Link, Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import './App.css';
import FavoritesPage from './components/FavoritesPage';
import verses from './data/verses.json';

const shuffleArray = (array) => {
  const arr = [...array];
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
};

const VerseBox = ({ verse, translatedVerse, reference, language, onSpeak, onStop, isSpeaking }) => (
  <div>
    <div className="verse-text mb-3" style={{ minHeight: '100px', fontSize: '1.25rem', color: '#333', fontStyle: 'italic' }}>
      <AnimatePresence mode="wait">
        <motion.p
          key={translatedVerse}
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -10 }}
          transition={{ duration: 0.6 }}
        >
          “{translatedVerse}”
        </motion.p>
      </AnimatePresence>
    </div>
    <div className="text-end fst-italic fw-semibold text-muted mb-3">— {reference}</div>
    <Button variant="info" onClick={isSpeaking ? onStop : onSpeak}>
      {isSpeaking ? '🔇 Stop' : '🔊 Speak'}
    </Button>
  </div>
);

const RandomVerseGenerator = ({ darkMode, toggleDarkMode }) => {
  const [shuffledVerses, setShuffledVerses] = useState([]);
  const [verseIndex, setVerseIndex] = useState(0);
  const [verse, setVerse] = useState('');
  const [translatedVerse, setTranslatedVerse] = useState('');
  const [language, setLanguage] = useState('en');
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem('favorites')) || []);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const synthRef = useRef(window.speechSynthesis);
  const utteranceRef = useRef(null);

  useEffect(() => {
    const newVerses = shuffleArray(verses);
    setShuffledVerses(newVerses);
    setVerse(newVerses[0]);
    setVerseIndex(1);
  }, []);

  useEffect(() => {
    if (verse) translateVerse(getVerseText(verse));
  }, [verse, language]);

  const getVerseText = (v) => v.split(' - ')[0] || '';
  const getReference = (v) => v.split(' - ')[1] || '';

  const translateVerse = async (text) => {
    if (language === 'en') {
      setTranslatedVerse(text);
      return;
    }
    try {
      const res = await axios.post('https://translate.argosopentech.com/translate', {
        q: text,
        source: 'en',
        target: language,
        format: 'text'
      }, {
        headers: { 'Content-Type': 'application/json' }
      });
      setTranslatedVerse(res.data.translatedText);
    } catch (error) {
      console.error('Translation error:', error);
      setTranslatedVerse(text);
    }
  };

  const generateNewVerse = () => {
    stopSpeaking();
    if (verseIndex >= shuffledVerses.length) {
      const reshuffled = shuffleArray(verses);
      setShuffledVerses(reshuffled);
      setVerse(reshuffled[0]);
      setVerseIndex(1);
    } else {
      setVerse(shuffledVerses[verseIndex]);
      setVerseIndex((prev) => prev + 1);
    }
  };

  const addToFavorites = () => {
    if (!favorites.includes(verse)) {
      const updated = [...favorites, verse];
      setFavorites(updated);
      localStorage.setItem('favorites', JSON.stringify(updated));
    }
  };

  const speakVerse = () => {
    stopSpeaking();
    const utterance = new SpeechSynthesisUtterance(translatedVerse);
    utterance.lang = language;
    utterance.onend = () => setIsSpeaking(false);
    synthRef.current.speak(utterance);
    utteranceRef.current = utterance;
    setIsSpeaking(true);
  };

  const stopSpeaking = () => {
    synthRef.current.cancel();
    setIsSpeaking(false);
  };

  const text = getVerseText(verse);
  const reference = getReference(verse);

  return (
    <div className={`d-flex justify-content-center align-items-center min-vh-100 ${darkMode ? 'bg-dark text-white' : 'bg-light'}`}>
      <div className={`verse-box p-5 rounded-4 shadow-lg text-center ${darkMode ? 'bg-secondary' : 'bg-white'}`} style={{ maxWidth: '700px', width: '90%' }}>
        <h1 className="title mb-4" style={{
          background: 'linear-gradient(to right, #00c6ff, #0072ff)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          fontWeight: 'bold',
          fontSize: '2.5rem'
        }}>
          Verse of Hope
        </h1>

        <VerseBox
          verse={text}
          translatedVerse={translatedVerse}
          reference={reference}
          language={language}
          onSpeak={speakVerse}
          onStop={stopSpeaking}
          isSpeaking={isSpeaking}
        />

        <div className="d-flex justify-content-center flex-wrap gap-2 mb-3">
          <select value={language} onChange={(e) => setLanguage(e.target.value)} className="form-select w-auto">
            <option value="en">English</option>
            <option value="te">Telugu</option>
            <option value="hi">Hindi</option>
          </select>
          <Button variant="primary" onClick={addToFavorites}>💾 Save</Button>
          <Button variant="outline-dark" onClick={toggleDarkMode}>{darkMode ? '☀️ Light' : '🌙 Dark'}</Button>
          <Link to="/favorites"><Button variant="success">View Favorites</Button></Link>
        </div>

        <Button
          className="px-4 py-2"
          variant="primary"
          onClick={generateNewVerse}
          style={{
            borderRadius: '25px',
            fontWeight: 'bold',
            background: 'linear-gradient(to right, #00c6ff, #0072ff)',
            border: 'none',
            transition: 'all 0.3s ease'
          }}
        >
          Generate Verse
        </Button>
      </div>
    </div>
  );
};

