import { AnimatePresence, motion } from 'framer-motion';
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import './App.css';
import versesData from './verses.json';

const synth = window.speechSynthesis;

const getStoredFavorites = () => {
  const stored = localStorage.getItem('favorites');
  return stored ? JSON.parse(stored) : [];
};

const App = () => {
  const [verses, setVerses] = useState([]);
  const [currentVerse, setCurrentVerse] = useState('');
  const [index, setIndex] = useState(0);
  const [favorites, setFavorites] = useState(getStoredFavorites());
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    const shuffled = [...versesData].sort(() => 0.5 - Math.random());
    setVerses(shuffled);
    setCurrentVerse(shuffled[0][language]);
    setIndex(1);
  }, [language]);

  const generateVerse = () => {
    if (index >= verses.length) {
      const reshuffled = [...versesData].sort(() => 0.5 - Math.random());
      setVerses(reshuffled);
      setIndex(1);
      setCurrentVerse(reshuffled[0][language]);
    } else {
      setCurrentVerse(verses[index][language]);
      setIndex(index + 1);
    }
  };

  const speakVerse = () => {
    if (synth.speaking) synth.cancel();
    const utter = new SpeechSynthesisUtterance(currentVerse);
    utter.lang = language === 'te' ? 'te-IN' : 'en-US';
    synth.speak(utter);
  };

  const toggleFavorite = () => {
    const updated = favorites.includes(currentVerse)
      ? favorites.filter(v => v !== currentVerse)
      : [...favorites, currentVerse];
    setFavorites(updated);
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  const isFavorite = favorites.includes(currentVerse);

  return (
    <div className="app-container">
      <div className="verse-box p-5 rounded-4 shadow-lg text-center">
        <h1 className="title mb-4">Verse of Hope</h1>

        <div className="verse-text mb-4" style={{ minHeight: '120px', fontSize: '1.2rem', fontStyle: 'italic' }}>
          <AnimatePresence mode="wait">
            <motion.p
              key={currentVerse}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.6 }}
            >
              {currentVerse}
            </motion.p>
          </AnimatePresence>
        </div>

        <div className="d-flex justify-content-center gap-3 flex-wrap">
          <Button onClick={generateVerse}>Generate Verse</Button>
          <Button onClick={speakVerse} variant="secondary">🔊 Speak</Button>
          <Button onClick={toggleFavorite} variant={isFavorite ? 'danger' : 'outline-danger'}>
            {isFavorite ? '❤️ Favorited' : '🤍 Favorite'}
          </Button>
          <select onChange={(e) => setLanguage(e.target.value)} value={language} className="form-select w-auto">
            <option value="en">English</option>
            <option value="te">Telugu</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default App;
